function linacre(){if(document.getElementById("tempMedia").value==""){alert("Entre com o valor da temperatura m√©dia.");return false}if(document.getElementById("ur").value==""){alert("Entre com o valor da umidade relativa.");return false}if(document.getElementById("altura").value==""){alert("Entre com o valor da altura.");return false}if((document.getElementById("latitude-graus").value=="")||document.getElementById("latitude-min").value==""){alert("Entre com os valores da latitude.");return false}var c=parseFloat(document.getElementById("tempMedia").value);var a=parseFloat(document.getElementById("ur").value);var d=parseFloat(document.getElementById("altura").value);var b=parseFloat(document.getElementById("latitude-graus").value);var k=parseFloat(document.getElementById("latitude-min").value);var f;f=((b+(k)/60));var j;j=((0.6108*(Math.exp((17.27*c)/(237.3+c))))*7.5);var e;e=(j*0.01*a);var i;i=(((237.3*Math.log10(e))-156.8)/(8.16-(Math.log10(e))));var g=0;g=(((500*(c+(0.006*d)))/(100-f))+(15*(c-i)))/(80-c);document.getElementById("resultado").value=(g.toFixed(2)+" mm/dia");return false};
