function tanque(){if(document.getElementById("bor").value==""){alert("Entre com o valor da bordadura!");return false}if((document.getElementById("bor").value<1)||(document.getElementById("bor").value>1000)){alert("Valor da bordadura deve ser entre 1m e 1000m.");return false}if(document.getElementById("ur").value==""){alert("Entre com o valor da umidade relativa!");return false}if((document.getElementById("ur").value<30)||(document.getElementById("vento").value>84)){alert("Valor da umidade relativa deve ser entre 30% e 84%.");return false}if(document.getElementById("vento").value==""){alert("Entre com o valor da velocidade do ventor!");return false}if((document.getElementById("vento").value<1)||(document.getElementById("vento").value>8)){alert("Valor da velocidade do vento deve ser entre 1m/s e 8m/s.");return false}if(document.getElementById("eca").value==""){alert("Entre com o valor do ECA!");return false}var a=parseFloat(document.getElementById("bor").value);var f=parseFloat(document.getElementById("ur").value);var e=parseFloat(document.getElementById("vento").value);var d=parseFloat(document.getElementById("eca").value);var c;c=(0.108-(0.0286*e)+(0.0422*Math.log(a)+(0.1434*Math.log(f))-(0.000631*(Math.pow(Math.log(a)),2)*Math.log(f))));var b;b=(c*d);document.getElementById("resultado").value=b.toFixed(2)+" mm/dia";return false};